-- ----------------------------
--  Table structure for `kzwp_postmeta`
-- ----------------------------
DROP TABLE IF EXISTS `kzwp_postmeta`;
CREATE TABLE `kzwp_postmeta` (
  `meta_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  `meta_key` varchar(255) DEFAULT NULL,
  `meta_value` longtext,
  PRIMARY KEY (`meta_id`),
  KEY `post_id` (`post_id`),
  KEY `meta_key` (`meta_key`)
) ENGINE=InnoDB AUTO_INCREMENT=3731 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

