DROP TABLE IF EXISTS `kzwp_posts`;
CREATE TABLE `kzwp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=319 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

-- ----------------------------
--  Records of `kzwp_posts`
-- ----------------------------
BEGIN;
INSERT INTO `kzwp_posts` VALUES ('1', '1', '2014-11-10 18:52:30', '2014-11-10 18:52:30', 'Ac rhoncus scelerisque phasellus lorem sit. Phasellus dictumst integer augue sit, in massa adipiscing dolor et turpis integer, aliquam risus nec nec mus in! Dolor ac tortor natoque! Integer a ac, aliquam odio, augue in aenean odio ut velit. Quis arcu eros a dolor? Ac. Pulvinar montes tortor ac placerat? Et urna lectus velit, magna mid, montes elit sed magnis, in in turpis. Velit tincidunt. Lundium a nunc, porttitor lorem sociis hac porttitor integer sed, et enim lorem aliquam phasellus. Vut sagittis mattis sociis diam cursus dapibus facilisis odio arcu? Cum! Mus, turpis ultrices sit rhoncus. Placerat a? Integer elementum.\r\n\r\nNunc ultrices rhoncus magnis dolor lundium natoque! Nunc parturient! Ut odio, duis porta, facilisis tristique, dignissim, a, platea urna elementum duis in magna enim ultrices! Vut amet amet, adipiscing a mauris, pellentesque! Vut enim, sed nec dapibus, proin lorem facilisis a tempor cursus? Dictumst mauris ut nec mauris sed! Dolor amet, lacus in penatibus, tincidunt amet scelerisque in ac elementum mus. Turpis cum urna et eros porttitor, sagittis hac lundium odio magnis turpis eros elementum, sed, rhoncus ultricies massa, penatibus ac, duis arcu, et placerat. Porttitor enim in, porta! Nunc et, elementum adipiscing etiam, lacus odio dis, a nunc.\r\n\r\nMattis risus dis? Et dolor odio odio, turpis amet, mattis dignissim, ut risus porttitor est etiam penatibus a porta, tristique dictumst mus eros arcu arcu tincidunt sagittis mattis phasellus, ac, vel, magnis turpis, augue hac purus. Adipiscing. A porttitor! Placerat nascetur, quis facilisis elit pid velit, sociis velit et a tristique. Scelerisque lectus egestas turpis nisi platea hac rhoncus a, sit vel nisi! Magna scelerisque dignissim pulvinar risus enim, odio nascetur purus adipiscing duis hac diam rhoncus. Sit mus, magna ut tristique dictumst tincidunt! Sagittis adipiscing nunc, in ultricies pulvinar et